from typing import Any, Dict


def merge_dicts(*dicts: Dict[str, Any]) -> Dict[str, Any]:
    """Merge multiple dictionaries, with later dicts overriding earlier ones on key conflicts.

    Args:
        *dicts: Variable number of dictionaries to merge.
            None values in the argument list are skipped.

    Returns:
        A new dictionary containing all key-value pairs from the inputs.

    Raises:
        TypeError: If any non-None argument is not a dictionary.
    """
    result: Dict[str, Any] = {}
    for d in dicts:
        if d is None:
            continue
        if not isinstance(d, dict):
            raise TypeError(f"Expected dict or None, got {type(d).__name__}")
        result.update(d)
    return result


# --- Test cases ---

# Basic merge
assert merge_dicts({"a": 1}, {"b": 2}) == {"a": 1, "b": 2}

# Override on conflict
assert merge_dicts({"a": 1}, {"a": 2}) == {"a": 2}

# Three dicts, last wins
assert merge_dicts({"a": 1}, {"a": 2, "b": 3}, {"a": 99}) == {"a": 99, "b": 3}

# None arguments skipped
assert merge_dicts({"a": 1}, None, {"b": 2}) == {"a": 1, "b": 2}

# All None
assert merge_dicts(None, None) == {}

# No arguments
assert merge_dicts() == {}

# Single dict
assert merge_dicts({"x": 42}) == {"x": 42}

# None as a *value* (not argument) is preserved
assert merge_dicts({"a": None}, {"b": 1}) == {"a": None, "b": 1}

# Override with None value
assert merge_dicts({"a": 1}, {"a": None}) == {"a": None}

# Empty dicts
assert merge_dicts({}, {}, {"a": 1}) == {"a": 1}

# Nested dicts (shallow merge — nested dicts are replaced, not deep-merged)
assert merge_dicts({"a": {"x": 1}}, {"a": {"y": 2}}) == {"a": {"y": 2}}

# Non-string keys
assert merge_dicts({1: "a"}, {2: "b"}) == {1: "a", 2: "b"}

# TypeError on bad input
try:
    merge_dicts({"a": 1}, "not a dict")
    assert False, "Should have raised TypeError"
except TypeError as e:
    assert "str" in str(e)

try:
    merge_dicts([1, 2, 3])
    assert False, "Should have raised TypeError"
except TypeError as e:
    assert "list" in str(e)

print("All tests passed.")
