from typing import Any, Dict

def merge_dicts(*dicts: Dict[str, Any]) -> Dict[str, Any]:
    """
    Merge multiple dictionaries into a single dictionary.

    Later dictionaries override earlier ones on key conflicts.
    Handles None values gracefully by skipping None arguments.

    Args:
        *dicts: Variable number of dictionaries to merge

    Returns:
        A new dictionary containing merged key-value pairs

    Raises:
        TypeError: If any non-None argument is not a dictionary
        ValueError: If no dictionaries are provided

    Examples:
        >>> merge_dicts({"a": 1}, {"b": 2})
        {"a": 1, "b": 2}
        >>> merge_dicts({"a": 1}, {"a": 2})
        {"a": 2}
        >>> merge_dicts({"a": 1}, None, {"b": 2})
        {"a": 1, "b": 2}
    """
    if not dicts:
        raise ValueError("At least one dictionary must be provided")

    # Filter out None values
    valid_dicts = [d for d in dicts if d is not None]

    if not valid_dicts:
        raise ValueError("All arguments were None; provide at least one dictionary")

    # Validate that all non-None items are dictionaries
    for i, d in enumerate(dicts):
        if d is not None and not isinstance(d, dict):
            raise TypeError(f"Argument at position {i} is not a dictionary: {type(d).__name__}")

    # Merge dictionaries
    result: Dict[str, Any] = {}
    for d in valid_dicts:
        result.update(d)

    return result


# Test cases
if __name__ == "__main__":
    # Basic merge
    assert merge_dicts({"a": 1}, {"b": 2}) == {"a": 1, "b": 2}

    # Override behavior
    assert merge_dicts({"a": 1}, {"a": 2}) == {"a": 2}

    # Multiple dicts
    assert merge_dicts({"a": 1}, {"b": 2}, {"c": 3}) == {"a": 1, "b": 2, "c": 3}

    # Override with multiple dicts
    assert merge_dicts({"a": 1, "b": 2}, {"b": 3, "c": 4}, {"c": 5}) == {"a": 1, "b": 3, "c": 5}

    # Single dictionary
    assert merge_dicts({"a": 1}) == {"a": 1}

    # Handle None values
    assert merge_dicts({"a": 1}, None, {"b": 2}) == {"a": 1, "b": 2}
    assert merge_dicts(None, {"a": 1}, None) == {"a": 1}

    # Empty dictionaries
    assert merge_dicts({}, {"a": 1}) == {"a": 1}
    assert merge_dicts({"a": 1}, {}) == {"a": 1}

    # Complex values
    assert merge_dicts({"a": [1, 2]}, {"b": {"nested": True}}) == {"a": [1, 2], "b": {"nested": True}}

    # Error cases
    try:
        merge_dicts()
        assert False, "Should raise ValueError"
    except ValueError as e:
        assert "At least one dictionary" in str(e)

    try:
        merge_dicts(None, None)
        assert False, "Should raise ValueError"
    except ValueError as e:
        assert "All arguments were None" in str(e)

    try:
        merge_dicts({"a": 1}, "not a dict")
        assert False, "Should raise TypeError"
    except TypeError as e:
        assert "not a dictionary" in str(e)

    print("All tests passed!")
