from typing import Dict, Any

def merge_dicts(*dicts: Dict[str, Any]) -> Dict[str, Any]:
    """
    Merge multiple dictionaries into a single dictionary.

    Later dictionaries override earlier ones on key conflicts.
    Handles None values gracefully by skipping them.

    Args:
        *dicts: Variable number of dictionaries to merge

    Returns:
        Dict[str, Any]: Merged dictionary

    Raises:
        TypeError: If non-dict arguments are passed

    Example:
        >>> merge_dicts({"a": 1}, {"b": 2})
        {'a': 1, 'b': 2}
        >>> merge_dicts({"a": 1}, {"a": 2})
        {'a': 2}
    """
    result: Dict[str, Any] = {}

    for d in dicts:
        # Handle None gracefully
        if d is None:
            continue

        # Validate that argument is a dictionary
        if not isinstance(d, dict):
            raise TypeError(f"Expected dict, got {type(d).__name__}")

        # Merge the dictionary
        result.update(d)

    return result


# Test cases
if __name__ == "__main__":
    # Basic merge
    assert merge_dicts({"a": 1}, {"b": 2}) == {"a": 1, "b": 2}

    # Override on conflict
    assert merge_dicts({"a": 1}, {"a": 2}) == {"a": 2}

    # Multiple dictionaries
    assert merge_dicts({"a": 1}, {"b": 2}, {"c": 3}) == {"a": 1, "b": 2, "c": 3}

    # Later dicts override earlier ones
    assert merge_dicts({"a": 1, "b": 2}, {"b": 3, "c": 4}) == {"a": 1, "b": 3, "c": 4}

    # Handle None values
    assert merge_dicts(None, {"a": 1}) == {"a": 1}
    assert merge_dicts({"a": 1}, None) == {"a": 1}
    assert merge_dicts(None, None) == {}

    # Empty dictionaries
    assert merge_dicts({}, {}) == {}
    assert merge_dicts({"a": 1}, {}) == {"a": 1}
    assert merge_dicts({}, {"a": 1}) == {"a": 1}

    # Single dictionary
    assert merge_dicts({"a": 1}) == {"a": 1}

    # No arguments
    assert merge_dicts() == {}

    # Complex values
    assert merge_dicts({"a": [1, 2]}, {"b": {"nested": True}}) == {"a": [1, 2], "b": {"nested": True}}

    # None as dictionary value (not None dict argument)
    assert merge_dicts({"a": None}, {"b": 2}) == {"a": None, "b": 2}

    # Error handling - invalid type
    try:
        merge_dicts({"a": 1}, "invalid")
        assert False, "Should raise TypeError"
    except TypeError as e:
        assert "Expected dict" in str(e)

    # Error handling - invalid type
    try:
        merge_dicts(123)
        assert False, "Should raise TypeError"
    except TypeError as e:
        assert "Expected dict" in str(e)

    print("All tests passed!")
